package loops;

public class multiplication {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=1;for(int i=1;i<=10;i++) {System.out.println(a +"x" +i+"=" + (a*i));}
		int b=2;for(int i=1;i<=10;i++) {System.out.println(b +"x" +i+"=" + (b*i));}
		int c=3;for(int i=1;i<=10;i++) {System.out.println(c +"x" +i+"=" + (c*i));}
		int d=4;for(int i=1;i<=10;i++) {System.out.println(d +"x" +i+"=" + (d*i));}
		int e=5;for(int i=1;i<=10;i++) {System.out.println(e +"x" +i+"=" + (e*i));}
		int f=5;for(int i=1;i<=10;i++) {System.out.println(f +"x" +i+"=" + (f*i));}
		int g=5;for(int i=1;i<=10;i++) {System.out.println(g +"x" +i+"=" + (g*i));}
	}

}
